var fileRender = function() {
	function _formatFileSize(bytes) {
		if (typeof bytes !== 'number') {
			return '';
		}
		if (bytes >= 1000000000) {
			return (bytes / 1000000000).toFixed(2) + ' GB';
		}
		if (bytes >= 1000000) {
			return (bytes / 1000000).toFixed(2) + ' MB';
		}
		return (bytes / 1000).toFixed(2) + ' KB';
	}

	function _formatBitrate(bits) {
		if (typeof bits !== 'number') {
			return '';
		}
		if (bits >= 1000000000) {
			return (bits / 1000000000).toFixed(2) + ' Gbit/s';
		}
		if (bits >= 1000000) {
			return (bits / 1000000).toFixed(2) + ' Mbit/s';
		}
		if (bits >= 1000) {
			return (bits / 1000).toFixed(2) + ' kbit/s';
		}
		return bits.toFixed(2) + ' bit/s';
	}

	function _formatTime (seconds) {
		var date = new Date(seconds * 1000),
			days = Math.floor(seconds / 86400);
		days = days ? days + 'd ' : '';
		return days +
			('0' + date.getUTCHours()).slice(-2) + ':' +
			('0' + date.getUTCMinutes()).slice(-2) + ':' +
			('0' + date.getUTCSeconds()).slice(-2);
	}

	function _formatPercentage (floatValue) {
		return (floatValue * 100).toFixed(2) + ' %';
	}

	function _renderExtendedProgress(data) {
		return _formatBitrate(data.bitrate) + ' | ' +
			_formatTime(
				(data.total - data.loaded) * 8 / data.bitrate
			) + ' | ' +
			_formatPercentage(
				data.loaded / data.total
			) + ' | ' +
			_formatFileSize(data.loaded) + ' / ' +
			_formatFileSize(data.total);
	}

	return {renderExtendedProgress: _renderExtendedProgress}
}()
// FONCTION POST REQUETE AJAX
function ajaxPost(url, data, callback) {
    var req = new XMLHttpRequest();
    req.open("POST", url);
    req.setRequestHeader("X-Requested-With", "XMLHttpRequest");
    req.addEventListener("load", function () {
        if (req.status >= 200 && req.status < 400) {
            callback(req.response);
        } else {
            console.error(req.status + " " + req.statusText + " " + url);
        }
    });
    req.addEventListener("error", function () {
        console.error("Erreur réseau avec l'URL " + url);
    });
    req.send(data);
}

function updateNbChars(el, limit) {
    element = document.getElementById(el);
    element.addEventListener('keyup', function () {
        countChars = this.nextSibling.nextSibling.querySelector('.countChars');
        chars = this.value.length;
        restant = limit - chars;
        if (chars >= limit) {
            restant = 0;
            countChars.style.color = "#a94442";
        } else {
            countChars.style.color = "#4CAF50";
        }
        countChars.innerText = " (" + restant + " caractères restants)";
    });
}

